<?php/** * Created by PhpStorm. * User: Administrator * Date: 2019/7/26 * Time: 11:23 */namespace app\admin\controller;use app\admin\model\Testimonials as TestimonialsModel;use think\Request;class Testimonials extends Base{    //列表页显示    public function index(Request $request)    {        if ($request->isAjax()) {            try {                $data=[];                if ($request->param('id')) {                    $data[] = ['id', '=', $request->param('id')];                }                if ($request->param('testimonials_name')) {                    $testimonials_name = $request->param('testimonials_name');                    $data[] = ['testimonials_name', 'like', "%$testimonials_name%"];                }                if ($request->param('testimonials_address')) {                    $testimonials_address = $request->param('testimonials_address');                    $data[] = ['testimonials_address', 'like', "%$testimonials_address%"];                }                $model = new TestimonialsModel();                $limit = $request->param('limit');                $info = $model->index($data,$limit);                $info = $info->toArray()['data'];                echo ajax($info, count($info), 'Success');            } catch (\Exception $exception) {                return $exception->getMessage();            }        }else{            return $this->fetch();        }    }    //添加    public function add_img(Request $request)    {        if ($request->isPost()) {            //取所有值 先验证            $arr = json_decode($request->param('data'), true);            //字段验证            if(empty($arr['testimonials_name'])){                return 2; //姓名不能为空            }else if(empty($arr['testimonials_address'])){                return 3; //所在地不能为空            }else if(empty($arr['testimonials_comment'])){                return 4; //评语不能为空            }else if(empty($arr['testimonials_img'])){                return 5; //可联系方式不能为空            }else{                $model = new TestimonialsModel();                $info = $model->add_img($arr);                return $info;            }        }        return $this->fetch();    }    //删除    public function img_del(Request $request)    {        if (!$request->param('id')) {            //参数不存在修改失败            return 2;        }else{            $model = new TestimonialsModel();            $info = $model->img_del($request->param('id'));            return $info;        }    }    //修改    public function update_edit(Request $request)    {        if ($request->isAjax()) {            //取所有值 先验证            $arr = json_decode($request->param('data'), true);            //字段验证            if(empty($arr['testimonials_name'])){                return 2; //姓名不能为空            }else if(empty($arr['testimonials_address'])){                return 3; //所在地不能为空            }else if(empty($arr['testimonials_comment'])){                return 4; //评语不能为空            }else{                $model = new TestimonialsModel;                $info = $model->updateedit($arr);                return $info;            }        }else{            //取轮播图find一条在修改前显示的数据            $model = new TestimonialsModel();            $arr = $model->updateinfo($request->param('id'));            $this->assign('arr', $arr);            return $this->fetch();        }    }}