<?php/** * Created by PhpStorm. * User: Administrator * Date: 2019/7/31 * Time: 15:26 */namespace app\admin\controller;use app\admin\model\Tikeco as TikecoModel;use think\Request;class Tikeco extends Base{    public function index(Request $request)    {        $model = new TikecoModel();        if($request->isAjax()){            //取所有值 先验证            $arr = json_decode($request->param('data'), true);            //字段验证            if(empty($arr['tikeco_title'])){                return 2; //标题不能为空            }else if(empty($arr['tikeco_keywords'])){                return 3; //关键词不能为空            }else if(empty($arr['tikeco_content'])){                return 4; //内容不能为空            }else{                $model = new TikecoModel;                $info = $model->updateedit($arr);                return $info;            }        }else{            $arr = $model->index();            $this->assign('arr', $arr);            return $this->fetch();        }    }}