<?php/** * Created by PhpStorm. * User: Administrator * Date: 2019/7/31 * Time: 15:26 */namespace app\admin\model;class Tikeco extends BaseModel{    public function index()    {        $info = $this->find();        return $info;    }    /**     * 执行修改     * @param $date     */    public function updateedit($date)    {        $date['update_time'] = date('Y-m-d H:i:s', time());        $info = $this->allowField(true)->isUpdate(true)->update($date);        if ($info) {            return 1; //修改成功        } else {            return 11; //修改失败        }    }}